CREATE TABLE BUISNESS_CATEGORY
(
bsn_catg_id VARCHAR2(6) NOT NULL,
bsn_catg_name VARCHAR2(26) NOT NULL,
bsn_catg_type VARCHAR2(26) NOT NULL,
CONSTRAINT BSN_CATG_PK PRIMARY KEY(bsn_catg_id),
CONSTRAINT BSN_TYPE_CK check(bsn_catg_type IN ('Restaurant','Music','Department'))
);
/
CREATE TABLE BUISNESS
(
buisness_id VARCHAR2(6) NOT NULL,
buisness_name VARCHAR2(126) NOT NULL,
street VARCHAR2(256) NOT NULL,
city VARCHAR2(32) NOT NULL,
states VARCHAR2(32) NOT NULL,
zip number(8,0) NOT NULL,
phone_no VARCHAR2(26) NOT NULL,
review_count number(8,0),
bsn_catg VARCHAR2(6) NOT NULL,
CONSTRAINT BUISNESS_PK PRIMARY KEY (buisness_id),
FOREIGN KEY (bsn_catg) REFERENCES BUISNESS_CATEGORY(bsn_catg_id)
 );
/

CREATE TABLE USER_YELP
(
user_id VARCHAR2(6) NOT NULL,
f_name VARCHAR2(26) NOT NULL,
l_name VARCHAR2(26) NOT NULL,
gender VARCHAR(1) NOT NULL,
birthplace VARCHAR2(26) NOT NULL,
dob DATE NOT NULL,
email VARCHAR2(32) NOT NULL,
CONSTRAINT USER_PK PRIMARY KEY (user_id),
CONSTRAINT GENDER_CK check(gender IN ('M','F'))

);
/

CREATE TABLE USER_FRIEND
(
friend_id VARCHAR2(6) NOT NULL,
f_user_id VARCHAR2(6) NOT NULL,
CONSTRAINT user_friend_PK PRIMARY KEY(friend_id, f_user_id),
CONSTRAINT USER_FRIEND_FK1 FOREIGN KEY (friend_id) REFERENCES USER_YELP(user_id),
CONSTRAINT USER_FRIEND_FK2 FOREIGN KEY (f_user_id) REFERENCES USER_YELP(user_id),

);
/
CREATE TABLE REVIEWS
(
  review_id VARCHAR2(6) NOT NULL,
  review_content VARCHAR2(26),
	rating NUMBER(1) NOT NULL,
  publish_date TIMESTAMP NOT NULL,
	rec_notrec VARCHAR2(8) NOT NULL,
  votes_funny NUMBER(1) NOT NULL,
  votes_cool NUMBER(1) NOT NULL,
  votes_useful NUMBER(1) NOT NULL,
  b_id VARCHAR2(6) NOT NULL,
  author_id VARCHAR2(6) NOT NULL,
	CONSTRAINT REVIEW_PK PRIMARY KEY (review_id),
  CONSTRAINT ck_rating check( rating>=1 AND rating<=5),
  CONSTRAINT ck_recnotrec check(rec_notrec IN ('N','R')),
	CONSTRAINT fk_b_id FOREIGN KEY (b_id) REFERENCES BUISNESS(buisness_id),
  CONSTRAINT fk_a_id FOREIGN KEY (author_id) REFERENCES USER_FRIEND(friend_id)
  );
/
CREATE TABLE PHOTO
(
	photo_id VARCHAR2(6) NOT NULL,
	author_id VARCHAR2(6) NOT NULL,
	description VARCHAR2(99) NULL,
	p_userid VARCHAR2(6) NOT NULL,
	p_rvwid VARCHAR2(6)NOT NULL,
	CONSTRAINT PHOTO_PK PRIMARY KEY(photo_id),
	FOREIGN KEY(author_id) REFERENCES USER_YELP(user_id),
	FOREIGN KEY(p_userid) REFERENCES USER_YELP(user_id),
	FOREIGN KEY(p_rvwid) REFERENCES REVIEWS(review_id)
);
/
CREATE TABLE Rates_USER_BUISNESS
(
user_id VARCHAR2(6) NOT NULL,
buisness_id VARCHAR2(6) NOT NULL,
scales Number(1),
CONSTRAINT user_BUISNESS_PK PRIMARY KEY(user_id,buisness_id),
CONSTRAINT ck_SCALES check( scales>=1 AND scales<=10),
CONSTRAINT user_BUISNESS_FK1 FOREIGN KEY (user_id) REFERENCES USER_YELP(user_id),
CONSTRAINT user_BUISNESS_FK2 FOREIGN KEY (buisness_id) REFERENCES BUISNESS(BUISNESS_ID)

);
/
CREATE TABLE USER_COMPLIMENTS_FRIENDS
