);
/
CREATE TABLE PROVIDE_USER_REVIEWS
(
USER_ID VARCHAR2(6) NOT NULL,
REVIEW_ID VARCHAR2(6) NOT NULL,
CONSTRAINT user_reviews_PK PRIMARY KEY(user_id, review_id),
CONSTRAINT user_reviews_FK1 FOREIGN KEY (user_id) REFERENCES USER_YELP(user_id),
